package main

import "fmt"

// ListNode ...
type ListNode struct {
	Val  int
	Next *ListNode
}

func longestValidParentheses(s string) int {
	maxLen := 0
	var j, curLen, lastFlag int
	if s == "" {
		return maxLen
	}

	for i := 0; i < len(s)-1; {
		j = i + 1
		if s[i] == 40 && s[j] == 41 {
			for len(s)-1-j >= 2 {
				if s[j+1] == 40 && s[j+2] == 41 {
					j += 2
					continue
				} else {
					break
				}
			}
			curLen = j - i + 1
			for l, r := i, j; l > 0 && r < len(s)-1; {
				l--
				r++
				if s[l] == 40 && s[r] == 41 {
					continue
				} else {
					curLen = r - l - 1
					break
				}
			}
			if curLen > maxLen {
				maxLen = curLen
			}
			i = j + 1
		}
		i++
	}

	return maxLen
}

func isValid(s string) bool {
	if s[0] != 40 || s[len(s)-1] != 41 {
		return false
	}

	left := []int32{}
	for _, r := range s {
		if r == 40 {
			left = append(left, r)
		} else if len(left) >= 1 {
			if r == 41 && left[len(left)-1] == 40 {
				left = left[:len(left)-1]
			} else {
				return false
			}
		} else {
			return false
		}
	}
	if len(left) > 0 {
		return false
	}
	return true
}

func printVals(head *ListNode) {
	fmt.Println("start")
	for head != nil {
		fmt.Println(head.Val)
		head = head.Next
	}
	fmt.Println("end")
}

